    [Parameter]
    public int indexOfAdult { get; set; }

    [Parameter]
    public int IdFamily { get; set; }

    private Adult adultToEdit = new Adult();
    private Family _family = new Family();

    protected override async Task OnParametersSetAsync()
    {
        _family = await familyService.GetFamilyAsync(IdFamily);
        for (int i = 0; i < _family.Adults.Count; i++)
        {
            if (_family.Adults[i].Id == indexOfAdult)
            {
                adultToEdit = _family.Adults[i];
            }
        }
    }

    public void Save()
    {
        for (int i = 0; i < _family.Adults.Count; i++)
        {
            if (_family.Adults[i].Id == adultToEdit.Id)
            {
                _family.Adults.Remove(_family.Adults[i]);
                _family.Adults.Add(adultToEdit);
            }
        }
        familyService.UpdateAsync(_family);
        NavigationManager.NavigateTo($"/FamilyInfo/{IdFamily}");
    }

    public void Remove()
    {
        for (int i = 0; i < _family.Adults.Count; i++)
        {
            if (_family.Adults[i].Id == adultToEdit.Id)
            {
                _family.Adults.Remove(_family.Adults[i]);
            }
        }
        familyService.UpdateAsync(_family);
        NavigationManager.NavigateTo($"/FamilyInfo/{IdFamily}");
    }


