                                           || filterById == null)).ToList();
    }
    
    private int PetCount(Family family)
    {
        int counter = 0;
        foreach (var child in family.Children)
        {
            counter += child.Pets.Count;
        }
        counter += family.Pets.Count;
        return counter;
    }

